import React, {Component} from "react"
import {Route, Switch, withRouter} from "react-router-dom";
import HomePage from "./pages/HomePage";
import ProjectPage from "./pages/ProjectPage";
import Footer from "./components/Footer";

import "./Pages.css"
import {CSSTransition, TransitionGroup} from "react-transition-group";
import TeamPage from "./pages/TeamPage";
import WorkPage from "./pages/WorkPage";
import ServicesPage from "./pages/ServicesPage";
import ContactPage from "./pages/ContactPage";
import CareersPage from "./pages/CareersPage";
import DigitalSolutionsPage from "./pages/DigitalSolutionsPage";

class Pages extends Component {

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (this.props.location.pathname !== prevProps.location.pathname) {
            this.prepAnimation()
            if ('scrollRestoration' in window.history) {
                window.history.scrollRestoration = 'manual';
            }
        }
    }

    prepAnimation = () => { // Get ready for the CSS animation

        let wrapper = document.querySelector('.wrapper:last-of-type');
        wrapper.style.height = '100vh'

        let offset = document.documentElement.scrollTop
        let page = document.querySelector('.wrapper:last-of-type .page')
        page.style.top = '-' + offset + 'px'

        // Set scroll top top
        window.scrollTo(0, 0)
    }


    render() {

        return (

            <TransitionGroup>
                <CSSTransition key={this.props.location.key} timeout={3000} className="wrapper">

                    <div className={"wrapper"}>
                        <div className={"page"}>

                            <Switch location={this.props.location}>
                                <Route exact path={"/"}>
                                    <HomePage/>
                                </Route>

                                <Route exact path={"/work"}>
                                    <WorkPage />
                                </Route>

                                <Route exact path={"/project/:name"}>
                                    <ProjectPage/>
                                </Route>

                                <Route exact path={"/digital-solutions"}>
                                    <DigitalSolutionsPage />
                                </Route>

                                <Route exact path={"/services"}>
                                    <ServicesPage />
                                </Route>

                                <Route exact path={"/team"}>
                                    <TeamPage />
                                </Route>

                                <Route exact path={"/contact"}>
                                    <ContactPage />
                                </Route>

                                <Route exact path={"/careers"}>
                                    <CareersPage />
                                </Route>

                            </Switch>

                            <Footer />

                        </div>
                    </div>

                </CSSTransition>
            </TransitionGroup>

        )

    }

}

export default withRouter(Pages)
